import React, { useState, useEffect, useRef } from 'react';
import data from './data';

const ChosungGame = () => {
  const [gameStarted, setGameStarted] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [songData, setSongData] = useState({});
  const [chosung, setChosung] = useState('');
  const [score, setScore] = useState(0);
  const [answerResult, setAnswerResult] = useState('');
  const [inputValue, setInputValue] = useState('');
  const [time, setTime] = useState(10);
  const [showTimer, setShowTimer] = useState(false);
  const inputRef = useRef(null);
  const timerRef = useRef(null);

  useEffect(() => {
    if (gameStarted && !gameOver) {
      const timer = setTimeout(() => {
        if (time > 0) {
          setTime((prevTime) => prevTime - 1);
        } else {
          handleGameOver();
        }
      }, 1000);

      timerRef.current = timer;

      return () => clearTimeout(timer);
    }
  }, [gameStarted, gameOver, time]);

  const startGame = () => {
    setGameStarted(true);
    setGameOver(false);
    setScore(0);
    generateChosung();
    startTimer();
  };

  const generateChosung = () => {
    const randomIndex = Math.floor(Math.random() * data.length);
    const selectedData = data[randomIndex];

    if (selectedData && selectedData.chosung && selectedData.hint) {
      setSongData(selectedData);
      setChosung(selectedData.chosung);
      setAnswerResult('');
      setInputValue('');
      if (inputRef.current) {
        inputRef.current.focus();
      }
    }
  };

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleEnterKeyPress = (event) => {
    if (event.key === 'Enter') {
      checkAnswer();
    }
  };

  const checkAnswer = () => {
    if (inputValue === songData.answer) {
      setScore((prevScore) => prevScore + 1);
      setAnswerResult('정답입니다!');
      clearTimeout(timerRef.current);
      setInputValue('');
      setShowTimer(true);
      setTimeout(() => {
        generateChosung();
        startTimer();
        setShowTimer(false);
      }, 3000);
    } else {
      setAnswerResult(`틀렸습니다. 정답은 "${songData.answer}"입니다.`);
      setInputValue('');
      setShowTimer(true);
      setTimeout(() => {
        handleGameOver();
        setShowTimer(false);
      }, 3000);
    }
  };

  const handleGameOver = () => {
    setGameStarted(false);
    setGameOver(true);
    clearTimeout(timerRef.current);
  };

  const handleTryAgain = () => {
    setGameStarted(false);
    setGameOver(false);
    setScore(0);
  };

  const startTimer = () => {
    setTime(10);
    timerRef.current = setTimeout(() => {
      handleGameOver();
    }, 10000);
  };

  return (
    <div>
      <h1>Chosung Game</h1>
      {!gameStarted && !gameOver && (
        <button onClick={startGame}>Start Game</button>
      )}
      {gameStarted && !gameOver && (
        <>
          <p>Chosung: {chosung}</p>
          <p>Hint: {songData.hint}</p>
          {showTimer && <p>{answerResult}</p>}
          {showTimer && <p>Time Remaining: {time} seconds</p>}
          <input
            type="text"
            value={inputValue}
            onChange={handleInputChange}
            onKeyPress={handleEnterKeyPress}
            ref={inputRef}
          />
        </>
      )}
      {gameOver && (
        <div>
          <p>Game Over</p>
          <p>Your Score: {score}</p>
          <button onClick={handleTryAgain}>Try Again</button>
        </div>
      )}
    </div>
  );
};

export default ChosungGame;
