import React, { useState, useEffect, useRef } from 'react';
import data from './data';

const ChosungGame = () => {
  const [gameStarted, setGameStarted] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [songData, setSongData] = useState({});
  const [chosung, setChosung] = useState('');
  const [hint, setHint] = useState('');
  const [score, setScore] = useState(0);
  const [answerResult, setAnswerResult] = useState('');
  const [inputValue, setInputValue] = useState('');
  const [time, setTime] = useState(10);
  const inputRef = useRef(null);
  const timerRef = useRef(null);

  useEffect(() => {
    if (gameStarted && !gameOver && time > 0) {
      timerRef.current = setInterval(() => {
        setTime((prevTime) => prevTime - 1);
      }, 1000);

      return () => clearInterval(timerRef.current);
    } else if (time === 0) {
      clearInterval(timerRef.current);
      if (answerResult === 'wrong') {
        setGameOver(true);
      } else {
        handleNextQuestion();
      }
    }
  }, [gameStarted, gameOver, time, answerResult]);

  useEffect(() => {
    if (gameStarted && !gameOver && answerResult === '') {
      timerRef.current = setTimeout(() => {
        handleNextQuestion();
      }, 3000);

      return () => clearTimeout(timerRef.current);
    }
  }, [gameStarted, gameOver, answerResult]);

  const startGame = () => {
    setGameStarted(true);
    setGameOver(false);
    setChosung('');
    setHint('');
  
    // 입력 창에 자동 포커스
    inputRef.current && inputRef.current.focus();
  };  

  const generateChosung = () => {
    const randomIndex = Math.floor(Math.random() * data.length);
    const selectedData = data[randomIndex];

    if (selectedData && selectedData.chosung && selectedData.hint) {
      setSongData(selectedData);
      setChosung(selectedData.chosung);
      setHint(selectedData.hint);
      setAnswerResult('');
      setInputValue('');
    }
  };

  const handleChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleKeyDown = (event) => {
    if (event.key === 'Enter') {
      clearInterval(timerRef.current);

      if (inputValue === songData.answer) {
        setScore((prevScore) => prevScore + 1);
        setAnswerResult('correct');
        setInputValue('');
        timerRef.current = setTimeout(() => {
          generateChosung();
          setTime(10);
        }, 3000);
      } else {
        setAnswerResult('wrong');
        setInputValue('');
        setTime(3);
      }
    }
  };

  const handleNextQuestion = () => {
    clearInterval(timerRef.current);
    generateChosung();
    setTime(10);
    setAnswerResult('');
    setInputValue('');
    inputRef.current.focus();
  };

  return (
    <div>
      <h1>Chosung Game</h1>
      {!gameStarted && !gameOver && (
        <button onClick={startGame}>Start Game</button>
      )}
      {gameStarted && !gameOver && (
        <>
          <div>
            <p>Chosung: {chosung}</p>
            <p>Hint: {hint}</p>
            <p>Time: {time} sec</p>
            <input
              ref={inputRef}
              type="text"
              value={inputValue}
              onChange={handleChange}
              onKeyDown={handleKeyDown}
            />
            {answerResult === 'correct' && (
              <p>정답입니다!</p>
            )}
            {answerResult === 'wrong' && (
              <p>틀렸습니다. 정답은 {songData.answer}입니다.</p>
            )}
          </div>
          <div>
            <p>Score: {score}</p>
          </div>
        </>
      )}
      {gameOver && (
        <div>
          <p>Game Over</p>
          <p>Final Score: {score}</p>
        </div>
      )}
    </div>
  );
};

export default ChosungGame;
