import React, { useState, useEffect, useRef } from 'react';
import data from './data';

const ChosungGame = () => {
  const [gameStarted, setGameStarted] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [songData, setSongData] = useState({});
  const [chosung, setChosung] = useState('');
  const [score, setScore] = useState(0);
  const [answerResult, setAnswerResult] = useState('');
  const inputRef = useRef(null);
  const timerRef = useRef(null);

  useEffect(() => {
    if (gameStarted && !gameOver) {
      timerRef.current = setTimeout(() => {
        setGameOver(true);
      }, 10000);

      return () => clearTimeout(timerRef.current);
    }
  }, [gameStarted, gameOver]);

  const startGame = () => {
    setGameStarted(true);
    setGameOver(false);
    setScore(0);
    generateChosung();
    inputRef.current.focus();
  };

  const generateChosung = () => {
    const randomIndex = Math.floor(Math.random() * data.length);
    const selectedData = data[randomIndex];

    if (selectedData && selectedData.chosung && selectedData.hint) {
      setSongData(selectedData);
      setChosung(selectedData.chosung);
      setAnswerResult('');
    }
  };

  const handleChange = (event) => {
    const { value } = event.target;
    if (value === songData.answer) {
      setScore(score + 1);
      setAnswerResult('correct');
      clearTimeout(timerRef.current);
      setTimeout(() => {
        generateChosung();
        inputRef.current.value = '';
        inputRef.current.focus();
      }, 3000);
    }
  };

  const handleGameOver = () => {
    setGameStarted(false);
    setGameOver(true);
    clearTimeout(timerRef.current);
  };

  const handleTryAgain = () => {
    setGameStarted(false);
    setGameOver(false);
    setScore(0);
  };

  const checkAnswer = (event) => {
    if (event.key === 'Enter') {
      const { value } = event.target;
      if (value === songData.answer) {
        setScore(score + 1);
        setAnswerResult('correct');
        clearTimeout(timerRef.current);
        setTimeout(() => {
          generateChosung();
          inputRef.current.value = '';
          inputRef.current.focus();
        }, 3000);
      } else {
        setAnswerResult('wrong');
        clearTimeout(timerRef.current);
        setTimeout(() => {
          generateChosung();
          inputRef.current.value = '';
          inputRef.current.focus();
        }, 3000);
      }
    }
  };

  return (
    <div>
      <h1>Chosung Game</h1>
      {!gameStarted && !gameOver && <button onClick={startGame}>Start Game</button>}
      {gameStarted && !gameOver && (
        <>
          <p>Chosung: {chosung}</p>
          <p>Hint: {songData.hint}</p>
          <input type="text" ref={inputRef} onChange={handleChange} onKeyDown={checkAnswer} />
        </>
      )}
      {gameOver && (
        <div>
          {answerResult === 'wrong' && (
            <>
              <p>Wrong Answer</p>
              <p>Correct Answer: {songData.answer}</p>
            </>
          )}
          {answerResult === 'correct' && <p>Correct Answer!</p>}
          <p>Your Score: {score}</p>
          <button onClick={handleTryAgain}>Try Again</button>
        </div>
      )}
    </div>
  );
};

export default ChosungGame;
