import React, { useState, useEffect, useRef } from 'react';
import data from './data';

const ChosungGame = () => {
  const [gameStarted, setGameStarted] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [songData, setSongData] = useState({});
  const [chosung, setChosung] = useState('');
  const [score, setScore] = useState(0);
  const [answerResult, setAnswerResult] = useState('');
  const [inputValue, setInputValue] = useState('');
  const [timer10s, setTimer10s] = useState(10);
  const [timer3s, setTimer3s] = useState(0);
  const inputRef = useRef(null);
  const timer10sRef = useRef(null);
  const timer3sRef = useRef(null);

  useEffect(() => {
    if (gameStarted && !gameOver) {
      timer10sRef.current = setInterval(() => {
        setTimer10s((prevTime) => {
          if (prevTime > 0) {
            return prevTime - 1;
          } else {
            clearInterval(timer10sRef.current);
            setGameOver(true);
            return prevTime;
          }
        });
      }, 1000);

      return () => clearInterval(timer10sRef.current);
    }
  }, [gameStarted, gameOver]);

  useEffect(() => {
    if (timer3s === 0) {
      clearInterval(timer3sRef.current);
      generateChosung();
      setTimer10s(10);
    }
  }, [timer3s]);

  useEffect(() => {
    if (gameStarted && !gameOver && inputRef.current) {
      inputRef.current.focus();
    }
  }, [gameStarted, gameOver]);

  const startGame = () => {
    setGameStarted(true);
    setGameOver(false);
    setScore(0);
    generateChosung();
    setTimer10s(10);
    setTimer3s(0);
  };

  const generateChosung = () => {
    const randomIndex = Math.floor(Math.random() * data.length);
    const selectedData = data[randomIndex];

    if (selectedData && selectedData.chosung && selectedData.hint) {
      setSongData(selectedData);
      setChosung(selectedData.chosung);
      setAnswerResult('');
      setInputValue('');
    }
  };

  const handleKeyDown = (event) => {
    if (event.key === 'Enter') {
      event.preventDefault();

      if (inputValue === songData.answer) {
        setScore((prevScore) => prevScore + 1);
        setAnswerResult('');
        clearInterval(timer10sRef.current);
        setTimer3s(3);
        timer3sRef.current = setInterval(() => {
          setTimer3s((prevTime) => prevTime - 1);
        }, 1000);
      } else {
        setAnswerResult(`틀렸습니다! 정답은 ${songData.answer}입니다.`);
        clearInterval(timer10sRef.current);
        setTimer3s(0);
        setTimeout(() => {
          generateChosung();
          setTimer10s(10);
        }, 3000);
      }
    }
  };

  const handleTryAgain = () => {
    setGameStarted(false);
    setGameOver(false);
    setScore(0);
    setTimer10s(10);
    setTimer3s(0);
  };

  return (
    <div>
      <h1>Chosung Game</h1>
      {!gameStarted && !gameOver && (
        <button onClick={startGame}>Start Game</button>
      )}
      {gameStarted && !gameOver && (
        <>
          <p>Chosung: {chosung}</p>
          <p>Hint: {songData.hint}</p>
          <p>Time Remaining (10s): {timer10s} seconds</p>
          {timer3s > 0 && <p>Time Remaining (3s): {timer3s} seconds</p>}
          <input
            type="text"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyDown={handleKeyDown}
            ref={inputRef}
          />
          <p>{answerResult}</p>
        </>
      )}
      {gameOver && (
        <div>
          <p>Game Over</p>
          <p>Your Score: {score}</p>
          <button onClick={handleTryAgain}>Try Again</button>
        </div>
      )}
    </div>
  );
};

export default ChosungGame;
