import React, { useState } from 'react';

const QuizComponent = ({ question, answer, onSubmit }) => {
  const [inputValue, setInputValue] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(inputValue.toLowerCase());
    setInputValue('');
  };

  return (
    <div>
      <h3>문제:</h3>
      <p>{question}</p>
      <form onSubmit={handleSubmit}>
        <label htmlFor="inputAnswer">초성 입력:</label>
        <input
          type="text"
          id="inputAnswer"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
        <button type="submit">정답 확인</button>
      </form>
      <p>힌트 정답: {answer}</p>
    </div>
  );
};

export default QuizComponent;
