import React, { useState, useEffect } from 'react';
import data from './data';

const ChosungGame = () => {
  const [gameStarted, setGameStarted] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [songData, setSongData] = useState({});
  const [chosung, setChosung] = useState('');
  const [hint, setHint] = useState('');
  const [score, setScore] = useState(0);
  const [time, setTime] = useState(10);

  useEffect(() => {
    if (gameStarted && !gameOver) {
      const timer = setTimeout(() => {
        if (time > 0) {
          setTime(time - 1);
        } else {
          setGameOver(true);
        }
      }, 1000);

      return () => clearTimeout(timer);
    }
  }, [gameStarted, gameOver, time]);

  const startGame = () => {
    setGameStarted(true);
    setGameOver(false);
    setTime(10);
    generateChosung();
  };

  const generateChosung = () => {
    const randomIndex = Math.floor(Math.random() * data.length);
    const selectedData = data[randomIndex];
  
    if (selectedData && selectedData.chosung && selectedData.hint) {
      setSongData(selectedData);
      setChosung(selectedData.chosung);
      setHint(selectedData.hint);
    }
  };  

  const handleChange = (event) => {
    const { value } = event.target;
    if (event.key === "Enter") {
      event.preventDefault();
      if (value === songData.answer) {
        setScore(score + 1);
        setTime(10);
        generateChosung();
      } else {
        setGameOver(true);
      }
    }
  };
      
  const handleTryAgain = () => {
    setGameStarted(false);
    setGameOver(false);
    setScore(0);
  };

  return (
    <div>
      <h1>Chosung Game</h1>
      {!gameStarted && !gameOver && (
        <button onClick={startGame}>Start Game</button>
      )}
      {gameStarted && !gameOver && (
        <>
          <p>Chosung: {chosung}</p>
          <p>Hint: {hint}</p>
          <p>Time: {time}</p>
          <input type="text" onKeyDown={handleChange} />
        </>
      )}
      {gameOver && (
        <div>
          <p>Game Over</p>
          <p>Your Score: {score}</p>
          <button onClick={handleTryAgain}>Try Again</button>
        </div>
      )}
    </div>
  );  
};

export default ChosungGame;
