import React, { useState, useEffect, useRef } from 'react';
import data from './data';

const ChosungGame = () => {
  const [gameStarted, setGameStarted] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [songData, setSongData] = useState({});
  const [chosung, setChosung] = useState('');
  const [score, setScore] = useState(0);
  const [time, setTime] = useState(10);
  const [showResult, setShowResult] = useState(false);
  const [resultMessage, setResultMessage] = useState('');
  const [correctAnswer, setCorrectAnswer] = useState('');

  const inputRef = useRef(null);

  useEffect(() => {
    if (gameStarted && !gameOver) {
      const timer = setTimeout(() => {
        if (time > 0) {
          setTime(time - 1);
        } else {
          setGameOver(true);
        }
      }, 1000);

      return () => clearTimeout(timer);
    }
  }, [gameStarted, gameOver, time]);

  useEffect(() => {
    if (gameStarted && !gameOver) {
      inputRef.current.focus();
    }
  }, [gameStarted, gameOver]);

  const startGame = () => {
    setGameStarted(true);
    setGameOver(false);
    setTime(10);
    generateChosung();
  };

  const generateChosung = () => {
    const randomIndex = Math.floor(Math.random() * data.length);
    const selectedData = data[randomIndex];

    if (selectedData && selectedData.chosung && selectedData.hint) {
      setSongData(selectedData);
      setChosung(selectedData.chosung);
      setShowResult(false);
      setResultMessage('');
      setCorrectAnswer('');
    }
  };

  const handleAnswer = (event) => {
    const { value } = event.target;
    if (event.key === 'Enter') {
      if (value === songData.answer) {
        setScore(score + 1);
        setResultMessage('정답입니다!');
        setTime(10);
      } else {
        setResultMessage('틀렸습니다!');
        setCorrectAnswer(songData.answer);
        setGameOver(true);
      }

      setShowResult(true);
      setTimeout(() => {
        if (!gameOver) {
          generateChosung();
          event.target.value = '';
          setShowResult(false);
        } else {
          setTimeout(() => {
            handleTryAgain();
          }, 3000);
        }
      }, 3000);
    }
  };

  const handleTryAgain = () => {
    setGameStarted(false);
    setGameOver(false);
    setScore(0);
  };

  if (gameOver) {
    return (
      <div>
        <h1>Chosung Game</h1>
        <div>
          <p>Game Over</p>
          <p>Your Score: {score}</p>
          <button onClick={handleTryAgain}>Try Again</button>
        </div>
      </div>
    );
  }

  return (
    <div>
      <h1>Chosung Game</h1>
      {!gameStarted && (
        <button onClick={startGame}>Start Game</button>
      )}
      {gameStarted && (
        <>
          <p>Chosung: {chosung}</p>
          <p>Hint: {songData.hint}</p>
          <p>Time: {time}</p>
          <input
            type="text"
            ref={inputRef}
            onKeyDown={handleAnswer}
            disabled={showResult}
          />
          {showResult && (
            <div>
              <p>{resultMessage}</p>
              {correctAnswer && <p>정답: {correctAnswer}</p>}
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default ChosungGame;
